@import './variable';
@import './mixin';


@font-face {
  font-family: IPixRegular;
  src: url("../font/IPixRegular.ttf");
  font-weight:800;
}
@font-face {
  font-family: VonwaonBitmap12;
  src: url("../font/VonwaonBitmap-12px.ttf");
  font-weight:1000;
}
@font-face {
  font-family: VonwaonBitmap16;
  src: url("../font/VonwaonBitmap-16px.ttf");
  font-weight:800;
}
@keyframes blink {
  from {
    visibility: hidden;
  }
  to {
    visibility: visible;
  }
}
#font-loader {
  font-family: VonwaonBitmap16;
  position: absolute;
  left: -1000px;
  visibility: hidden;
}

body {
  margin: 0;
  padding: 0;
  font-family: VonwaonBitmap16;
  font-size: 40px;
}
#game-view {
  background-image: url('../images/bg.png');
  display: flex;
  height: 100vh;
  align-items: center;
  justify-content: center;
  color: $main-color;
  .tips {
    width: 100%;
    text-align: center;
    margin-top: 20%;
    font-size: 30px;
    // 闪烁，但好像没生效 orz
    visibility: visible;
    animation: blink 1s step-start infinite;
  }
  canvas {
    background-image: url('../images/bg.png');
  }
  @media screen and (max-width: 900px) {

    canvas {
      width: 100vw;
    }
  }
  @import './modal';
  @import "./welcome";
  @import './home';
  @import './setting';
}

sources.../public/style/modal.scss:
.modal {
  @keyframes text-end {
    0% {
      content: '_';
    }
    100% {
      content: '';
    }
  }
  position: absolute !important;
  border: $border-style;
  background-image: url('../images/bg.png');
  box-shadow: $btn-box-shadow;
  .btn-box {
    display: flex !important;
    position: absolute !important;
    justify-content: space-between;
    .btn {
      @include btn-style;
      height: 50px;
      width: 260px;
      padding: 16px;
      text-align: center;
      &:hover {
        top: 4px;
        position: relative;
      }
    }
  }
  &.dialogue {
    width: 90%;
    bottom: 2%;
    height: 220px;
    padding: 20px;
    left: 50%;
    transform: translateX(-50%) !important;
    @media screen and (max-width: 900px) {
      transform: scale(0.48) translate(58%, 20%) !important;
    }
    .btn-box {
      bottom: 12px;
      width: calc(100% - 40px);
    }
  }
  &.big {
    width: 60%;
    height: 60%;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%) !important;
    #modal-text {
      padding: 70px;
    }
    .btn-box {
      bottom: 36px;
      width: 100%;
      justify-content: space-around;
      text-align: center;
      .btn {
        border: $border-style;
        width: 160px;
        padding: 6px;
        line-height: 50px;
        &:hover {
          box-shadow: $btn-box-shadow;
        }
      }
    }
  }
  #modal-text {
    display: inline-block;
    line-height: 55px;
    &::after {
      content: "_";
      animation: text-end 1s infinite;
    }
  }
}

sources.../public/style/mixin.scss:
@mixin bg-image($url, $width, $height: $width) {
  width: $width;
  height: $height;
  background-image: url($url);
  background-size: contain;
}

@mixin btn-style {
  border: $border-style;
  cursor: pointer;
  &:hover {
    box-shadow: $btn-box-shadow;
    transition: all 0.15s;
  }
}

@mixin icon-style {
  cursor: pointer;
  &:hover {
    transform: translateY(10%);
  }
}

